package net.n2oapp.framework.autotest.control;

import net.n2oapp.framework.api.metadata.global.view.widget.table.ShapeTypeEnum;
import net.n2oapp.framework.api.metadata.meta.control.TextPositionEnum;
import net.n2oapp.framework.autotest.api.component.page.SimplePage;
import net.n2oapp.framework.autotest.api.component.snippet.Image;
import net.n2oapp.framework.autotest.api.component.widget.FormWidget;
import net.n2oapp.framework.autotest.run.AutoTestBase;
import net.n2oapp.framework.config.N2oApplicationBuilder;
import net.n2oapp.framework.config.metadata.pack.*;
import net.n2oapp.framework.config.selective.CompileInfo;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

/**
 * Автотест компонента вывода изображения
 */
class ImageFieldAT extends AutoTestBase {

    private SimplePage page;

    @BeforeAll
    static void beforeClass() {
        configureSelenide();
    }

    @BeforeEach
    @Override
    public void setUp() {
        super.setUp();

        page = open(SimplePage.class);
        page.shouldExists();
    }

    @Override
    protected void configure(N2oApplicationBuilder builder) {
        super.configure(builder);
        builder.packs(new N2oPagesPack(), new N2oApplicationPack(), new N2oWidgetsPack(), new N2oFieldSetsPack(), new N2oControlsPack());
        builder.sources(
                new CompileInfo("net/n2oapp/framework/autotest/control/image_field/index.page.xml"));
    }

    @Test
    void testImageField() {
        Image image = page.widget(FormWidget.class).fields().field(Image.class);
        image.shouldExists();
        image.shouldHaveTitle("Заголовок");
        image.shouldHaveDescription("описание");
        image.shouldHaveUrl("data:image/png;base64,");
        image.shouldHaveWidth(40);
        image.shouldHaveTextPosition(TextPositionEnum.right);
        image.shouldHaveShape(ShapeTypeEnum.ROUNDED);

        image = page.widget(FormWidget.class).fields().field(1, Image.class);
        image.shouldExists();
        image.shouldHaveTitle("Заголовок");
        image.shouldHaveDescription("описание");
        image.shouldHaveUrl(getBaseUrl() + "/images/hamburg-3846525__340.jpg");
        image.shouldHaveTextPosition(TextPositionEnum.bottom);
        image.shouldHaveShape(ShapeTypeEnum.CIRCLE);
    }

}
